// Solution using forEach
Array.prototype.myFilter = function(callback) {
  var newArray = [];
  this.forEach(item => {if(callback(item)) newArray.push(item)});
  return newArray;
}

var new_s = s.myFilter(function(item){
  return item % 2 === 1;
